CREATE TABLE `travels_db`.`travels` (
  `id` INT NOT NULL,
  `Description` VARCHAR(1000) NULL DEFAULT NULL,
  `WhereTo` VARCHAR(200) NOT NULL,
  `Image` LONGTEXT NULL DEFAULT NULL,
  `From` DATETIME NOT NULL,
  `To` DATETIME NOT NULL,
  `Price` INT NOT NULL,
  PRIMARY KEY (`id`));



